mapper.update:
UPDATE transaction
payment_option = #{paymentOption:CHAR} , receiver_name = #{receiverName:CHAR} , receiver_phone = #{receiverPhone:VARCHAR} , demailaddr = #{divyAddr:VARCHAR} , dlvy_request = #{divyRequest:VARCHAR} , dlvy_date = #{divyDate:DATE}
WHERE tran_no = #{tranNo}

mapper.select:

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT tran_no , prod_no , buyer_id , payment_option , receiver_name, receiver_phone, 
											demailaddr, dlvy_request, tran_status_code, order_data, dlvy_date
										FROM transaction
											
												buyer_id = #{buyer.userId}
											
										 ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*) 
  	FROM(	SELECT buyer_id , receiver_name , receiver_phone, tran_status_code
					FROM transaction
						
							buyer_id = #{buyer.userId}
						
					 ) countTable
